import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://localhost:5279/api/todos";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    axios.get(API_URL)
      .then((res) => setTodos(res.data))
      .catch((error) => console.error("API Error:", error)); // ✅ Add error handling
  }, []);

  const addTodo = () => {
    if (!title.trim()) return; // ✅ Prevent empty todos

    axios.post(API_URL, { title, isCompleted: false })
      .then((res) => {
        setTodos([...todos, res.data]);
        setTitle("");
      })
      .catch((error) => console.error("Add Todo Error:", error)); // ✅ Add error handling
  };

  const deleteTodo = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(() => {
        setTodos(todos.filter((todo) => todo.id !== id));
      })
      .catch((error) => console.error("Delete Todo Error:", error)); // ✅ Add error handling
  };

  return (
    <div>
      <h1>To-do List</h1>
      <input value={title} onChange={(e) => setTitle(e.target.value)} />
      <button onClick={addTodo}>Add</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            {todo.title}
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
